import { useRouter } from 'next/router'
import { memo } from 'react'
import { Row, Col, List, Pagination } from 'antd'
import Link from 'next/link'
import Repo from '../components/Repo'
const api = require('../lib/api')

const LANGUAGES = ['JavaScript', 'TypeScript', 'HTML', 'CSS', 'Java', 'Rust', 'Python'];
const SORT_TYPES = [
  {
    name: 'Best Match',
  },
  {
    name: 'Most Stars',
    value: 'stars',
    order: 'desc',
  },
  {
    name: 'Fewest Stars',
    value: 'stars',
    order: 'asc',
  },
  {
    name: 'Most Forks',
    value: 'forks',
    order: 'desc',
  },
  {
    name: 'Fewest Forks',
    value: 'forks',
    order: 'asc',
  },
];
const selectedItemStyle = {
    borderLeft: '2px solid #e36209',
    fontWeight: 100,
  }

const FilterLink = memo(({ name, query, lang, sort, order, page }) => {
    let queryString = `?query=${query}`;
    lang && (queryString += `&lang=${lang}`);
    sort && (queryString += `&sort=${sort}&order=${order || 'desc'}`);
    page && (queryString += `&page=${page}`);
    console.log(name)
    return (
        <Link href={`/search${queryString}`}>
            <a>{name}</a>
        </Link>
    )
})

export default function Search({ repos }) {
    const router = useRouter();
    const { ...querys } = router.query;
    const { sort, order, lang, page } = router.query;

    return (
        <>
            <Row gutter={20}>
                <Col span={6}>
                    <List 
                        bordered
                        header={<span className="list-header">Languages</span>}
                        style={{ marginBottom: 20 }}
                        dataSource={LANGUAGES}
                        renderItem={item => {
                            const selected = item === lang;
                            return  (
                                <List.Item style={selected ? selectedItemStyle : null}>
                                    {selected ? <span>{item}</span> : 
                                        <FilterLink 
                                            {...querys}
                                            lang={lang}
                                            name={item}
                                        />
                                    }
                                </List.Item>
                        )}}
                    />

                    <List 
                        bordered
                        header={<span className="list-header">Sort By</span>}
                        dataSource={SORT_TYPES}
                        renderItem={item => {
                            let selected;
                            if (item.name === 'Best Match' && !sort) {
                                selected = true;
                            } else if (item.name === sort && item.order === order) {
                                selected = true;
                            }
                            return (
                                <List.Item style={selected ? selectedItemStyle : null}>
                                    {selected ? <span>{item.name}</span> : 
                                        <FilterLink 
                                            {...querys}
                                            name={item.name}
                                            sort={item.sort}
                                            order={item.order}
                                        />
                                    }
                                </List.Item>
                        )}}
                    />
                </Col>
                <Col span={18}> 
                    <h3 className="repos-title">{repos.total_count} 个仓库</h3>
                    {repos.items.map(repo => (
                        <Repo repo={repo} key={repo.id} />
                    ))}
                    <div>
                        <Pagination 
                            pageSize={30}
                            current={+page || 1}
                            total={repos.total_count}
                            itemRender={(page, type, ol) => {
                                const p = type === 'page' ? page : type === 'prev' ?  page - 1 : page + 1;
                                const name = type === 'page' ? page : ol;
                                return <FilterLink {...querys} page={p} name={name} />
                            }}
                        />
                    </div>
                </Col>
            </Row>
        </>
    )
}

Search.getInitialProps = async ({ query, req, res }) => {
    console.log('Search getInitialProps called')
    const { q, sort, lang, order, page} = query
    if (!q) {
        return {
            repos: {
                total_count: 0
            }
        }
    }
    // ?q=react+language:javascript&sort=stars&order=desc&page=2
    let queryString = `?q=${q}`;
    lang && (queryString += `+language:${lang}`);
    sort && (queryString += `&sort=${sort}&order=${order || 'desc'}`);
    page && (queryString += `&page=${page}`);

    const result = api.request({
        url: `/search/repositories${queryString}`
    }, req, res)

    return {
        repos: result.data
    }
}
