import Link from 'next/link'
import { Icon } from 'antd'
import moment from 'moment'

// import { getLastUpdated } from '../lib/utils'

export default ({ repo }) => {
    const openIssuesCount = repo.open_issues_count;
	return (
		<>
			<div className="repo-info">
				<h3>
					<Link href={`/detail?owner=${repo.owner.login}&name=${repo.name}`}>
                        <a>
                            <span className="repo-title-owner">{repo.owner.login}</span>
                            <span> / </span>
                            <span className="repo-title">{repo.name}</span>
                        </a>
					</Link>
				</h3>
				<p className="repo-desc">{repo.description}</p>
				<div className="other-info">
                    <span className="lang">{repo.language}</span>
                    <span className="stars">
                        <Icon type="star" theme="filled" />
                        {repo.stargazers_count} 
                    </span>
                    <span className="forks">
                        <Icon type="fork" />
                        {repo.forks_count}
                    </span>
                    <span className="last-updated">
						{/* {getLastUpdated(repo.updated_at)} */}
						{moment(repo.updated_at).format("LL")}
					</span>
                    {openIssuesCount > 0 &&
                        <span className="open-issues">
                            {openIssuesCount} {openIssuesCount > 1 ? 'issues need help' : 'issue needs help'}
                        </span>
                    }
                </div>
            </div>
            <style jsx>{`
                .repo-info { 
                    border-bottom: 1px solid #ddd;
                    padding: 15px;
                }
                .repo-title-owner {
                    font-size: 20px;
                    color: #0366d6;
                    font-weight: 400;
                }
                .repo-title {
                    font-size: 20px;
                    color: #0366d6;
                    font-weight: 500;
                }
                .other-info > span {
                    margin-right: 12px;
                    font-size: 12px;
                }
                .repo-desc {
                    max-width: 550px;
                    font-size: 14px;
                } */}
            `}</style>
	    </>
    )
}
