import Link from 'next/link'
import Repo from '../../components/Repo'
import api from '../../lib/api'

function Detail({repoBasic}) {
    console.log(repoBasic)
    return (
        <div className="root">
            <div className="repo-basic">
                <Repo repo={repoBasic}/>
            </div>
            <div className="tabs">
                <Link href="detail">
                    <a className="index">Readme</a>
                </Link>
                <Link href="detail/issues">
                    <a className="issues">Issues</a>
                </Link>
            </div>
            <div>Readme</div>
        </div>
    )
}

Detail.getInitialProps = async ({ query, req, res }) => {
    const { name, owner } = query;
    const repoBasicResp = await api.request({
        url: `/repos/${owner}${name}`
    }, req, res);
    return {
        repoBasic: repoBasicResp.data
    };
}
