import Router, { useRouter } from 'next/router'
import { Row, Col, List } from 'antd'
import Link from 'next/link'
const api = require('../lib/api')

const LANGUAGES = ['JavaScript', 'TypeScript', 'HTML', 'CSS', 'Java', 'Rust', 'Python'];
const SORT_TYPES = [
  {
    name: 'Best Match',
  },
  {
    name: 'Most Stars',
    value: 'stars',
    order: 'desc',
  },
  {
    name: 'Fewest Stars',
    value: 'stars',
    order: 'asc',
  },
  {
    name: 'Most Forks',
    value: 'forks',
    order: 'desc',
  },
  {
    name: 'Fewest Forks',
    value: 'forks',
    order: 'asc',
  },
];
const selectedItemStyle = {
    borderLeft: '2px solid #e36209',
    fontWeight: 100,
  }

export default function Search({ repos }) {
    const router = useRouter();
    const { sort, order, lang, query } = router.query

    const handleLanguageChange = (language) => {
        Router.push({
            pathname: '/search',
            query: {
                query,
                lang: language,
                sort,
                order,
            }
        })
    }
    const handleSortChange = (sort) => {
        Router.push({
            pathname: '/search',
            query: {
                query,
                lang,
                sort: sort.value,
                order: sort.order,
            }
        })
    }

    return (
        <>
            <Row gutter={20}>
                <Col span={6}>
                    <List 
                        bordered
                        header={<span className="list-header">Languages</span>}
                        style={{ marginBottom: 20 }}
                        dataSource={LANGUAGES}
                        renderItem={item => {
                            const selected = item === lang;
                            return  (
                                <List.Item style={selected ? selectedItemStyle : null}>
                                    {/* <Link href="/search"> */}
                                        <a onClick={() => handleLanguageChange(item)}>{item}</a>
                                    {/* </Link> */}
                                </List.Item>
                        )}}
                    />

                    <List 
                        bordered
                        header={<span className="list-header">Sort By</span>}
                        dataSource={SORT_TYPES}
                        renderItem={item => {
                            let selected;
                            if (item.name === 'Best Match' && !sort) {
                                selected = true;
                            } else if (item.name === sort && item.order === order) {
                                selected = true;
                            }
                            return (
                                <List.Item style={selected ? selectedItemStyle : null}>
                                    {/* <Link href="/search"> */}
                                        <a onClick={() => handleSortChange(item)}>{item.name}</a>
                                    {/* </Link> */}
                                </List.Item>
                        )}}
                    />
                </Col>
            </Row>
        </>
    )
}

Search.getInitialProps = async ({ query, req, res }) => {
    console.log('Search getInitialProps called')
    const { q, sort, lang, order, page} = query
    if (!q) {
        return {
            repos: {
                total_count: 0
            }
        }
    }
    // ?q=react+language:javascript&sort=stars&order=desc&page=2
    let queryString = `/?q=${q}`;
    lang && (queryString += `+language:${lang}`);
    sort && (queryString += `&sort=${sort}&order=${order || 'desc'}`);
    page && (queryString += `&page=${page}`);

    const result = api.request({
        url: `/search/repositories${queryString}`
    }, req, res)

    return {
        repos: result.data
    }
}
