import { useRouter } from 'next/router'
import { memo, isValidElement } from 'react'
import { Row, Col, List, Pagination, Statistic } from 'antd'
import Link from 'next/link'
import Repo from '../components/Repo'
import data from '../testData'
const api = require('../lib/api')

const LANGUAGES = ['JavaScript', 'TypeScript', 'HTML', 'CSS', 'Java', 'Rust', 'Python'];
const SORT_TYPES = [
  {
    name: 'Best Match',
  },
  {
    name: 'Most Stars',
    value: 'stars',
    order: 'desc',
  },
  {
    name: 'Fewest Stars',
    value: 'stars',
    order: 'asc',
  },
  {
    name: 'Most Forks',
    value: 'forks',
    order: 'desc',
  },
  {
    name: 'Fewest Forks',
    value: 'forks',
    order: 'asc',
  },
];
const per_page = 20;
const selectedItemStyle = {
    borderLeft: '2px solid #e36209',
    fontWeight: 100,
  }
const initRepos = {
    total_count: 0,
    items: []
};
function noop() {}

const FilterLink = memo(({ name, q, lang, sort, order, page }) => {
    let queryString = `?q=${q}`;
    lang && (queryString += `&lang=${lang}`);
    sort && (queryString += `&sort=${sort}&order=${order || 'desc'}`);
    page && (queryString += `&page=${page}`);
    queryString += `&per_page=${per_page}`;

    return (
        <Link href={`/search${queryString}`}>
            {isValidElement(name) ? name : <a>{name}</a>}
        </Link>
    )
});

export default function Search({ repos = initRepos }) {
    const router = useRouter();
    const { ...querys } = router.query;
    const { sort, order, lang, page } = router.query;

    return (
        <div className="root">
            <Row gutter={20}>
                <Col span={6}>
                    <List 
                        bordered
                        header={<span className="list-header">Languages</span>}
                        style={{ marginBottom: 20 }}
                        dataSource={LANGUAGES}
                        renderItem={item => {
                            const selected = item === lang;
                            return  (
                                <List.Item style={selected ? selectedItemStyle : null}>
                                    {selected ? <span>{item}</span> : 
                                        <FilterLink 
                                            {...querys}
                                            lang={item}
                                            name={item}
                                        />
                                    }
                                </List.Item>
                        )}}
                    />

                    <List 
                        bordered
                        header={<span className="list-header">Sort By</span>}
                        dataSource={SORT_TYPES}
                        renderItem={item => {
                            let selected = false;
                            if (item.name === 'Best Match' && !sort) {
                                selected = true;
                            } else if (item.name === sort && item.order === order) {
                                selected = true;
                            }
                            return (
                                <List.Item style={selected ? selectedItemStyle : null}>
                                    {selected ? <span>{item.name}</span> : 
                                        <FilterLink 
                                            {...querys}
                                            name={item.name}
                                            sort={item.value}
                                            order={item.order}
                                        />
                                    }
                                </List.Item>
                        )}}
                    />
                </Col>
                <Col span={18}> 
                    <h3 className="repos-title">
                        <Statistic value={repos.total_count}/> repository results
                    </h3>
                    {repos.items.map(repo => (
                        <Repo repo={repo} key={repo.id} />
                    ))}
                    <div className="pagination">
                        <Pagination 
                            pageSize={per_page}
                            current={+page || 1}
                            total={1000} //github限制返回1000条
                            onChange={noop}
                            itemRender={(page, type, ol) => {
                                const p = type === 'page' ? page : type === 'prev' ?  page - 1 : page + 1;
                                const name = type === 'page' ? page : ol;
                                return (
                                    <FilterLink 
                                        {...querys} 
                                        page={p} 
                                        name={name} 
                                    />
                                )
                            }}
                        />
                    </div>
                </Col>
            </Row>
            <style jsx>{`
                .root {
                    padding: 20px 0;
                }
                .repos-title {
                    border-bottom: 1px solid #eee;
                    font-size: 24px;
                    line-height: 50px;
                }
                .list-header {
                    font-weight: 700;
                    font-size: 16px;
                }
                .pagination {
                    padding: 20px;
                    text-align: center;
                }
            `}</style>
        </div>
    )
}

Search.getInitialProps = async ({ query, req, res }) => {
    console.log('Search getInitialProps called')
    const { q, sort, lang, order, page} = query
    if (!q) {
        return {
            repos: initRepos
        }
    }
    // ?q=react+language:javascript&sort=stars&order=desc&page=2
    let queryString = `?q=${q}`;
    lang && (queryString += `+language:${lang}`);
    sort && (queryString += `&sort=${sort}&order=${order || 'desc'}`);
    page && (queryString += `&page=${page}`);
    queryString += `&per_page=${per_page}`;

    const result = await api.request({
        url: `/search/repositories${queryString}`
    }, req, res)
    
    return {
        repos: result.data || data.react_repos
    }
}
