import { useSelector } from 'react-redux'
import { Button, Tabs } from 'antd'
import { useState, useEffect } from 'react'
import Repo from '../components/Repo'
import config from '../config'
import LRU from 'lru-cache'
import { useRouter, Router } from 'next/router'

const { OAUTH_URL } = config;
const { TabPane } = Tabs;
const cache = new LRU({
    maxAge: 1000 * 60 * 30 //30min
})
const isServer = typeof window === undefined

export default function Index() {
    const user = useSelector(state => state.user);
    const initLoggedIn = user && user.id;
    const [userRepos, setUserRepos] = useState([]);
    const [userStarredRepos, setUserStarredRepos] = useState([]);
    const [loggedIn, setLoggedIn] = useState(initLoggedIn);
    const router = useRouter();
    const activeTabKey = router.query.key || 'repos';

    const handleTabChange = (activeTabKey) => {
        Router.push(`/?key=${activeTabKey}`)
    }

	useEffect(() => {
        if (loggedIn) { 
            if (cache.get('userRepos') && cache.get('userStarredRepos')) {
                return;
            }
            (async () => {
                const userRepos = await api.request(
                    {
                        url: '/user/repos'
                    }, 
                )
                const userStarredRepos = await api.request(
                    {
                        url: '/user/starred'
                    }
                )
                setUserRepos(userRepos.data);
                setUserStarredRepos(userStarredRepos.data)
            })()
        }
    }, [loggedIn])
    
    useEffect(() => {
        if (!isServer) { //服务端环境不使用缓存
            userRepos && cache.set('userRepos', userRepos)
            userStarredRepos &&  cache.set('userStarredRepos', userStarredRepos)
        }
    }, [userRepos, userStarredRepos])

	const BeforeLogin = () => (
		<>
			<div className="root">
				<Button href={OAUTH_URL} onClick={() => setLoggedIn(true)}>
                    未登录，请点击登录
                </Button>
			</div>
			<style jsx>{`
				.root {
					display: flex;
					justify-content: center;
					align-items: center;
				}
			`}</style>
		</>
	)
	
	if (!loggedIn) {
		return <BeforeLogin />
	}

	return (
		<>
			<div className="root">
				<div className="user-info">
					<img src={user.avatar_url} alt="user avatar" className="user-avatar"/>
					<p className="user-name">{user.login}</p>
				</div>
                <Tabs activeKey={activeTabKey} onChange={handleTabChange}>
                    <TabPane key="repos" tab="Repositories">
                        {/* <div className="user-repos"> */}
                            {userRepos.map(repo => <Repo key={repo.id} repo={repo} />)}
                        {/* </div> */}
                    </TabPane>
                    <TabPane key="stars" tab="Stars">
                        {userStarredRepos.map(repo => <Repo key={repo.id} repo={repo} />)}
                    </TabPane>
                </Tabs>
			</div>
			<style jsx>{`
				.root {
					display: flex;
					align-items: flex-start;
					padding: 20px 0;
				}
				.user-info {
					width: 200px;
					margin-right: 40px;
				}
				.user-name {
					font-size: 20px;
					font-weight: 300;
					margin-top: 10px;
				}
				.user-avatar {
					width: 100%;
					border-radius: 4px;
				}
			`}</style>
		</>
	)
}

const api = require('../lib/api')
//调用： 服务端渲染index时 /客户端导航到index时
Index.getInititalProps = async ({ ctx, reduxStore }) => {
    //浏览器环境： axios.get('http://localhost:3000')
    //node环境： axios.get('http://localhost:80')
    const result = await api.request(
        {
            url: '/user/repos'
        }, 
        ctx.req, 
        ctx.res
    )

    return {
        data: result.data
    }
}
