import withRepoBasic from '../../components/with-repo-basic'
import api from '../../lib/api'
import { Avatar, Button } from 'antd'
import { useState, useCallback } from 'react'
import Markdown from '../../components/Markdown'
import moment from 'moment'

function IssueDetail({ issue }) {
    return (
        <div className="root">
            <Markdown content={issue.body} />
            <div className="actions">
                <Button href={issue.html_url} target="_blank">
                    open issues page
                </Button>
            </div>
            <style jsx>{`
                .root {
                    background: #fefefe;
                    padding: 20px;
                }
                .actions {
                    text-align: right;
                }
            `}</style>
        </div>
    )
}

function IssueItem({ issue }) {
    const [showDetail, setShowDetail] = useState(false);

    const toggleShowDetail = useCallback(() => {
        setShowDetail(pre => !pre)
    }, [])

    return (
        <>
            <div className="issue">
                <Button
                    type="primary"
                    size="small"
                    style={{ position: 'absolute', right: 10, top: 10 }}
                    onClick={toggleShowDetail}
                >
                    {showDetail ? 'hide' : 'display'}
                </Button>
                <div className="avatar">
                    <Avatar
                        src={issue.user.avatar_url}
                        shape="square"
                        size={50}
                    />
                </div>
                <div className="main-info">
                    <h6>
                        <span>{issue.title}</span>
                    </h6>
                    <p className="sub-info">
                        <span>Updated at {moment(issue.updated_at).format("LL")}</span>
                    </p>
                </div>
                <style jsx>{`
                .issue {
                    display: flex;
                    position: relative;
                    padding: 10px;
                }    
                .issue:hover {
                    background: #fafafa;
                }
                .issue + .issue {
                    border-top: 1px solid #ddd;
                }
                .main-info > h6 {
                    max-width: 600px;
                    font-size: 16px;
                    padding-right: 40px;
                }
                .avatar {
                    margin-right: 20px;
                }
                .sub-info {
                    margin-bottom: 0;
                }
                .sub-info > span + span {
                    display: inline-block;
                    margin-left: 20px;
                    font-size: 12px;
                }
            `}</style>
            </div>
            {showDetail ? <IssueDetail issue={issue} /> : null}
        </>
    )
}

function Issues({ issues }) {
    console.log(issues)
    return (
        <div className="root">
            <div className="issues">
                {issues.map(issue => <IssueItem key={issue.id} issue={issue} />)}
            </div>
        </div>
    )
}

Issues.getInitialProps = async ({ query, req, res }) => {
    const { owner, name } = query;
    const issuesResp = await api.request({
        url: `/repos/${owner}/${name}/issues`
    }, req, res);

    return {
        issues: issuesResp.data,
    }
}

export default withRepoBasic(Issues)
