import React from 'react'
import createSore from '../store/store'

const isServer = typeof window === 'undefined'
const __NEXT_REUDX_STORE__ = '__NEXT_REUDX_STORE__'

function getOrCreateStore(initialState) {
    //服务端每次都要创建一个新的store
    if (isServer) {
        return createSore(initialState)
    }
    //客户端是唯一的store
    if (!window[__NEXT_REUDX_STORE__]) {
        window[__NEXT_REUDX_STORE__] = createSore(initialState)
    }
    return window[__NEXT_REUDX_STORE__]
}

export default Comp => {
    class WithReduxApp extends React.Component {
        constructor(props) {
            super(props)
            this.reduxStore = getOrCreateStore(props.initialReduxState)
        }

        render() {
            const { Component, pageProps, ...rest } = this.props

            // console.log(Component, pageProps)

            if (pageProps) {
                pageProps.test = '123'
            }

            return (
                <Comp
                    Component={Component}
                    pageProps={pageProps}
                    {...rest}
                    reduxStore={this.reduxStore}
                />
            )
        }
    }

    WithReduxApp.getInitialProps = async ctx => {
        const reduxStore = getOrCreateStore()

        ctx.reduxStore = reduxStore

        let appProps = {}
        if (typeof Comp.getInitialProps === 'function') {
            appProps = await Comp.getInitialProps(ctx)
        }

        return {
            ...appProps,
             //getInitialProps返回的props会序列化成字符串写在html传给客户端，
            //客户端会读取此字符串反序列化成一个JS对象（生成 store对象）
            //不能直接返回store（里面有方法，不能序列化）
            initialReduxState: reduxStore.getState(),
        }
    }

    return WithReduxApp
}
