import { useSelector } from 'react-redux'
import { Button } from 'antd'
import { useEffect } from 'react'
import Repo from '../components/Repo'

export default function Index({ data }) {
	const user = useSelector(state => state.user)
	let userRepos = []
	useEffect(async () => {
		const result = await api.request(
			{
				url: '/user/repos'
			}, 
		)
		userRepos = result.data
	}, [])

	const BeforeLogin = () => (
		<>
			<div className="root">
				<Button>未登录，请点击登录</Button>
			</div>
			<style jsx>{`
				.root {
					display: flex;
					justify-content: center;
					align-items: center;
				}
			`}</style>
		</>
	)
	
	if (!user || !user.id) {
		return <BeforeLogin />
	}

	return (
		<>
			<div className="root">
				<div className="user-info">
					<img src={user.avatar_url} alt="user avatar" className="user-avatar"/>
					<p className="user-name">{user.login}</p>
				</div>
			</div>
			<style jsx>{`
				.root {
					display: flex;
					align-items: flex-start;
					padding: 20px 0;
				}
				.user-info {
					width: 200px;
					margin-right: 40px;

				}
				.user-name {
					font-size: 20px;
					font-weight: 300;
					margin-top: 10px;
				}
				.user-avatar {
					width: 100%;
					border-radius: 4px;
				}
			`}</style>
			<div className="user-repos">
				{userRepos.map(repo => <Repo repo={repo}/>)}
			</div>
		</>
	)
}

const api = require('../lib/api')
const isServer = typeof window === undefined

//调用： 服务端渲染index时 /客户端导航到index时
Index.getInititalProps = async ({ ctx, reduxStore }) => {
    //浏览器环境： axios.get('http://localhost:3000')
    //node环境： axios.get('http://localhost:80')
    const result = await api.request(
        {
            url: '/user/repos'
        }, 
        ctx.req, 
        ctx.res
    )
    console.log(result)
    return {
        data: result.data
    }
}
