import Link from 'next/link'
import Repo from '../../components/Repo'
import api from '../../lib/api'
import { useRouter } from 'next/router'

function makeQueryStr(queryObj) {
    const query = Object.entries(queryObj)
        .reduce((result, entry) => {
            result.push(entry.join('='));
            return result
        }, []).join('&')
        return `?${query}`
}

export default function Detail({ repoBasic }) {
    const router = useRouter();
    const queryStr = makeQueryStr(router.query)
    return (
        <div className="root">
            <div className="repo-basic">
                <Repo repo={repoBasic}/>
            </div>
            <div className="tabs">
                <Link href={`detail${queryStr}`}>
                    <a className="tab index">Readme</a>
                </Link>
                <Link href={`detail/issues${queryStr}`}>
                    <a className="tab issues">Issues</a>
                </Link>
            </div>
            <div>Readme</div>
            <style jsx>{`
                .root {
                    padding-top: 20px;
                }    
                .repo-basic {
                    border-radius: 5px;
                }
                .tab {
                    margin-left: 20px;
                }
            `}</style>
        </div>
    )
}

Detail.getInitialProps = async ({ query, req, res}) => {
    const { name, owner } = query;
    const repoBasicResp = await api.request({
        url: `/repos/${owner}/${name}`
    }, req, res);
    return {
        repoBasic: repoBasicResp.data
    };
}
