import Link from 'next/link'
import { Layout, Input, Icon, Avatar, Tooltip, Dropdown, Menu } from 'antd'
import { useState, useCallback } from 'react'
import Container from './Container'
import { useSelector, useDispatch } from 'react-redux'
import getConfig from 'next/config'
import { logout } from '../store/store'
import axios from 'axios'
import { useRouter} from 'next/router'

const { publicRuntimeConfig } = getConfig()
const { Header, Content, Footer } = Layout
const footerStyle = {
    textAlign: 'center'
}
const githubIconStyle = {
    color: '#fff',
    fontSize: 40,
    display: 'block',
    paddingTop: 10,
    marginRight: 20,
}

const AppLayout = ({ children }) => {
    const [search, setSearch] = useState('')
    const user = useSelector(state => state.user)
    const dispatch = useDispatch()

    const handleSearchChange = (e) => {
        setSearch(e.target.value)
    }

    const handleOnSearch = (e) => {
        
    }

    const handleGotoOAuth = (e) => {
        e.preventDefault()
        axios.get(`/prepare-auth?url=${useRouter().asPath}`)
            .then(resp => {
                if (resp.status === 200) {
                    location.href = publicRuntimeConfig.OAUTH_URL
                } else {
                 console.log('prepare auth failed', resp)
                }
            })
            .catch(err => {
                console.log('prepare auth failed', err)
            })
            
    }
    const handleLogout = useCallback(() => {
        dispatch(logout())
    },[])

    const userDropdown = (
        <Menu onClick={handleLogout}> 
            <Menu.Item>
                登出
            </Menu.Item>
        </Menu>
    )

    return (
        <Layout>
            <Header>
                <Container renderer={<div className="header-inner"/>}>
                    <div className="header-left">
                        <div className="logo">
                            {/* <Link href="/"> */}
                                <Icon type="github" style={githubIconStyle} />
                            {/* </Link> */}
                        </div>
                        <div>
                            <Input.Search 
                                placeholder="搜索或跳转..."
                                value={search}
                                onChange={handleSearchChange}
                                onSearch={handleOnSearch}
                            />
                        </div>
                    </div>
                    <div className="header-right">
                        <div className="user">
                            {user && user.id ? (
                                <Dropdown overlay={userDropdown}>
                                    <Avatar size={40} src={user.avatar_url} />
                                </Dropdown>
                            ) : (
                                <Tooltip title="点击登录">
                                    <a href={publicRuntimeConfig.OAUTH_URL} onClick={handleGotoOAuth}>
                                        <Avatar size={40} icon="user" />
                                    </a>
                                </Tooltip>
                            )}
                        </div>
                    </div>
                </Container>
            </Header>
            <Content>
                {/* 改变组件的渲染类型，不能指定props */}
                {/* <Container comp="div">{children}</Container> */}
                <Container >{children}</Container>
            </Content>
            <Footer style={footerStyle}>
                Hello Tara@<a href="mailto:taralovecats@163.com">taralovecats@163.com</a>
            </Footer>
            <style jsx>{`
                .header-inner {
                    display: flex;
                    justify-content: space-between;
                }
                .header-left {
                    display: flex;
                    display: flex-start;
                }
            `}</style>  
            <style jsx global>{`
                #__next {
                    height: 100%;
                }
                .ant-layout {
                    height: 100%;
                }
                .ant-layout-header {
                    padding-left: 0,
                    padding-right: 0
                }
            `}</style>
        </Layout>
    )
}

export default AppLayout
