import App from 'next/app'
import 'antd/dist/antd.css' //add global css
import Layout from '../components/Layout'
import { Provider } from 'react-redux'  
import withRedux from '../lib/with-redux'
import Router from 'next/router'
import { Spin } from 'antd'
import { useState, useEffect } from 'react'
import axios from 'axios'

const COLORS = {};
function MyApp({ Component, pageProps, reduxStore }) {
    //Keeping state when navigating pages
    const [loading, setLoading] = useState(false);

    const startLoading = () => {
        setLoading(true)
    }
    const stopLoading = () => {
        setLoading(false)
    }

    useEffect(() => {
        Router.events.on('routeChangeStart', startLoading)
        Router.events.on('routeChangeComplete', stopLoading)
        Router.events.on('routeChangeError', stopLoading)
        return () => {
            Router.events.off('routeChangeStart', startLoading)
            Router.events.off('routeChangeComplete', stopLoading)
            Router.events.off('routeChangeError', stopLoading)
        }
    }, [])

    useEffect(() => {
        axios.get('/colors')
            .then(resp => COLORS = resp.data)
            .catch(err => console.log(err))
    }, [])

    return (
        <Provider store={reduxStore}>
            {/* Persisting layout between page changes */}
            <Layout>
                <Spin tip="loading..." spinning={loading}>
                    <Component {...pageProps} colors={COLORS}/>
                </Spin>
            </Layout>
        </Provider>
    )
}


//define this method because blocking data requirements are needed
//for every single page in this application
MyApp.getInitialProps = async (appContext) => {
    //calls page's `getInitialProps` and fills `appProps.pageProps` 
    const appProps = await App.getInitialProps(appContext);

    return {
        ...appProps,
    }
}

export default withRedux(MyApp)
